import React, {Component} from "react";
import axios from "axios";
import Moment from "moment";
import ReactMarkdown from 'react-markdown';

const PostStyle = {
  height: "80vh",
  padding: "5vh 15vw 5vh 15vw",
  overflowY: "scroll",
  color: "#ebdbb2"
}

const CommentStyle = {
  backgroundColor: "#1d2021",
  boxShadow: "5px 5px 10px black",
  padding: "2vh 1vh 2vh 1vh",
  margin: "2vh 0 2vh 1vh",
  borderRadius: "15px"
}

export default class Post extends Component {
  state = {
    api: [],
    Post: this.props.location.state,
  }

  componentDidMount() {
    axios.get('http://localhost:9000/comments')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }
  render() {
    return(
      <div id="Post" style={PostStyle}>
        <h1>{this.state.Post.Title}</h1>
        <h2>Par {this.state.Post.Writer} le {this.state.Post.Date}</h2>
        <ReactMarkdown>{this.state.Post.Body}</ReactMarkdown>
        <div id="Comments">
          {this.state.api.map(api => 
            {
            if (api.PostID === this.state.Post.id)
              return (
                <div id="Comment" style={CommentStyle}>
                  <p>{ api.Commenter + " Le " + Moment(api.CDate).format('DD/MM/YYYY') + " à " + Moment(api.CDate).format('HH:mm')}</p>
                  <p>{api.Cbody}</p>
                </div>
              )
            }
          )}
        </div>
      </div>
    )}
}
