import React, { Component } from "react";

const PostsStyle = {
  height: "90vh",
  width: "100vw",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  justifyContent: "flex-start",
}

const PostStyle = { 
  notHover: {
    width: "60vw",
    height: "25vh",
    borderRadius: "10px",
    color: "#ebdbb2",
    backgroundColor: "#1d2021",
    boxShadow: "5px 5px 10px black ",
    margin: "10vh 0 0 0",
    padding: "0 0 0 2vh"
  },
  hover: {

  }
}


export default class ShowPosts extends Component {

  hoverStyle() {
    this.style=PostStyle.hover
  }

  render() {
    let postData = this.props.posts
    return (
      <div id="Posts" style={PostsStyle}>
        {postData.map(posts =>
          <div class="Post" style={PostStyle} onMouseOver={hoverStyle}>
            <h2 id="PostTitle">{posts.PTitle}</h2>
            <p id="PostBody">{posts.PBody.substring(0, 300)+"..."}</p>
          </div>
        )}
      </div>
    );
  };
}
