import React, {Component} from 'react';

const CommentStyle = {
  display: "flex",
  flexDirection: "column",
  backgroundColor: "#1d2021",
  borderRadius: "15px",
  padding: "3vh",
}

const TextStyle = {
  margin: "1vh",
}

const TextAreaStyle = {
  row: "4",
}

const ButtonStyle = {
  width: "15vw",
  height: "4vh",
  marginLeft: "auto",
  border: "none",
  borderRadius: "10px",
  backgroundColor: "#fe8019",
  color: "#1d2021",
  cursor: "pointer",
}

export default class CommentBox extends Component {

  render() {
    return (
      <form action="" style={CommentStyle}>
        <input type="text" placeholder="anonymous" style={TextStyle}/>
        <textarea placeholder="I think you are the worst writer in existence blah blah blah" style={TextAreaStyle}/>
        <button style={ButtonStyle}>Comment</button>
      </form>
    )
  }
}
