import React, { Component } from "react";
import axios from "axios";

export default class ShowPosts extends Component {
  render() {
    let postData = this.props.data
    return (
      <div id="Posts">
        {postData.map(posts =>
          <div class="Post">
            <h2 id="PostTitle">{posts.Ptitle}</h2>
            <p id="PostBody">{posts.PBody}</p>
          </div>
        )}
      </div>
    );
  };
}
