import React, { Component } from 'react';
import axios from 'axios';


export default class login extends Component {
  state = {
    username: '',
    password: '',
    confirmPassword: '',
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    var data = {
      username: this.state.username,
      password: this.state.password
    }
    const url = "http://localhost/inscription"
    axios({
      method: 'POST',
      url: url,
      data: data,
      headers: {
        'Access-Control-Allow-Origin:': '*',
        'Content-Type': 'application/json',
      }
    })
      .catch(err => alert(err));
  }

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <input type="text" name="username" onChange={this.onChange} placeholder="Pseudonyme"/>
        <input type="password" name="password" onChange={this.onChange} placeholder="Mot de passe"/>
        <input type="password" name="confirmPassword" onChange={this.onChange} placeholder="Confirmez le mot de passe"/>
        <button type="submit">Cr√©er un compte</button>
      </form>
    )
  }
}
