import React, { Component } from "react";
import axios from "axios";

export default class showPosts extends Component {
  state = {
    posts: []
  }

  componentDidMount() {
    axios.get('https://localhost:9000/posts')
      .then(res => {
        const posts = res.data;
        this.setState({ posts })
      });
  }

  render() {
    return (
      <div id="Posts">
        {this.state.posts.map(posts =>
          <div class="Post">
            <h2 id="PostTitle">{posts.Ptitle}</h2>
            <p id="PostBody">{posts.PBody}</p>
          </div>)
        }
      </div>
    );
  };
}
