import React, {Component} from 'react';
import axios from "axios";

const CommentStyle = {
  display: "flex",
  flexDirection: "column",
  backgroundColor: "#1d2021",
  borderRadius: "15px",
  padding: "3vh",
  boxShadow: " 5px 5px 10px black"
}

const TextAreaStyle = {
  width: "100%",
  borderRadius: "7px",
  border: "none",
  margin: "2vh 0 3vh 0",
  backgroundColor: "#504945",
  color: "#ebdbb2",
  padding: "1vh",
  fontSize: "14px",
  resize: "none"
}

const ButtonStyle = {
  width: "15vw",
  height: "4vh",
  marginLeft: "auto",
  border: "none",
  borderRadius: "7px",
  backgroundColor: "#fe8019",
  color: "#1d2021",
  cursor: "pointer",
}

export default class CommentBox extends Component {
  state = {
    postID: this.props.post,
    onSubmit: this.HandleSubmit.bind(this),
  }

  HandleSubmit(e) {
    e.preventDefault();
    axios({
      url: '/sendcomment',
      method: 'POST',
      data: {
        commenter: 'User',
        post: this.props.post,
        cbody: this.refs.cbody,
      }
    })
  }

  render() {
    return (
      <form onSubmit={this.onSubmit} style={CommentStyle}>
        <label>User</label>
        <textarea rows="4" placeholder="I think you are the worst writer in existence blah blah blah" style={TextAreaStyle}/>
        <button style={ButtonStyle} type="submit">Comment</button>
      </form>
    )
  }
}
