import React, {Component} from "react";
import axios from "axios";
import Moment from "moment";
import ReactMarkdown from "react-markdown";
import CommentBox from "./Comment.js";

const PostStyle = {
  height: "80vh",
  padding: "5vh 15vw 5vh 15vw",
  overflowY: "scroll",
  color: "#ebdbb2"
}

const CommentStyle = {
  backgroundColor: "#1d2021",
  boxShadow: "5px 5px 10px black",
  padding: "2vh",
  margin: "2vh 0 2vh 0",
  borderRadius: "15px"
}

export default class Post extends Component {
  state = {
    api: [],
    Post: this.props.location.state,
    comments: true,
  }

  componentDidMount() {
    axios.get('http://localhost:9000/comments')
      .then(res => {
        this.setState({ api: res.data })
      })
      .catch(err => console.log(err)) 
  }

  render() {
    return(
      <div id="Post" style={PostStyle}>
        <h1>{this.state.Post.PTitle}</h1>
        <h2>Par {this.state.Post.Writer} le {this.state.Post.Date}</h2>
        <ReactMarkdown>{this.state.Post.Body}</ReactMarkdown>
        <div id="Comments">
          <h2>Commentaires</h2>
          {
            this.state.api.map(api => 
            { var i = 0;
              if (api.PostID === this.state.Post.id){
                i += 1;
                return (
                  <div id="Comment" style={CommentStyle}>
                    <p>{ `${api.Commenter} Le ${Moment(api.CDate).format('DD/MM/YYYY')} à ${Moment(api.CDate).format('HH:mm')}`}</p>
                    <p>{api.Cbody}</p>
                  </div>
                )
              }
              if (i === 0) {
                return this.setState({comments: false})
              }
            }
          )
        }
          {this.state.comments ? '' : <p>Pas de commentaire pour le moment... Ecrivez en un !</p>}
          <CommentBox post={this.state.Post.id}/>
        </div>
      </div>
    )}
}
