import React, { Component } from 'react';
import axios from 'axios'


export default class login extends Component {
  state = {
    username: '',
    password: '',
    confirmPassword: '',
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const url = "/inscription"
    const user = {
      username: this.state.username,
      password: this.state.password
    }

    axios.post(url,user)
  }

  render() {
    const { username, password, confirmPassword } = this.state;
    return(
      <form onSubmit={this.handleSubmit} method="post">
        <input type="text" name="username" value={username} onChange={this.onChange} placeholder="Pseudonyme"/>
        <input type="password" name="password" value={password} onChange={this.onChange} placeholder="Mot de passe"/>
