import React, {Component} from "react";
import axios from "axios";

export default class Post extends Component {
  state = {
    api: [],
    PostID: this.props.location.state,
  }

  componentDidMount() {
    axios.get('http://localhost:9000/blogapi')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }
  render() {
    return(
      this.state.api.map(api =>
          <h1>{api.PTitle}</h1>
    )) 
  }
}
