import React, { Component } from "react";
import { Link } from "react-router-dom";
import Moment from 'moment';

const PostsStyle = {
  height: "90vh",
  width: "100vw",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  justifyContent: "flex-start",
}

const PostStyle = { 
  width: "60vw",
  height: "25vh",
  borderRadius: "10px",
  color: "#ebdbb2",
  backgroundColor: "#1d2021",
  boxShadow: "5px 5px 10px black ",
  margin: "10vh 0 0 0",
  padding: "0 0 0 2vh",
  cursor: "pointer"
};

export default class ShowPosts extends Component {
  state = {
    api: []
  }

  componentDidMount() {
    axios.get('http://localhost:9000/blogapi')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }

  render() {
    return (
      <div id="Posts" style={PostsStyle}>
        {this.state.api.map(api =>
        <Link to="" class="/Post" style={PostStyle}>
          <h2 id="PostTitle">{api.PTitle}</h2>
          <p id="author">{"Par " + api.PostWriterName + " le " + Moment(api.Pdate).format('DD/MM/YYYY')}</p>
          <p id="PostBody">{api.PBody.substring(0, 150)+"..."}</p>
        </Link>
        )}
      </div>
    );
  };
}
