import React, {Component} from "react";
import axios from "axios";
import Moment from "moment";
import ReactMarkdown from 'react-markdown';

const PostStyle = {
  height: "80vh",
  padding: "5vh 15vw 5vh 15vw",
  overflowY: "scroll",
  color: "#ebdbb2"
}

const body = "## héhéhé \nYoyaye"

export default class Post extends Component {
  state = {
    api: [],
    Post: this.props.location.state,
  }

  componentDidMount() {
    axios.get('http://localhost:9000/blogapi')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }
  render() {
    return(
      <div id="Post" style={PostStyle}>
        <h1>{this.state.Post.Title}</h1>
        <h2>Par {this.state.Post.Writer} le {this.state.Post.Date}</h2>
        <ReactMarkdown>{this.state.Post.Body}</ReactMarkdown>
        <div id="Comments">
          {this.state.api.map(api =>
            {
              if (this.state.api.PostID === this.state.Post.Id)
                return <p>{ this.state.api.Commenter + " " + Moment(this.state.api.Cdate).format('DD/MM/YYYY')}</p>
            }
          )}
        </div>
      </div>
    )}
}
