import React, { Component } from 'react';
import axios from 'axios';


export default class login extends Component {
  state = {
    username: '',
    password: '',
    confirmPassword: '',
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    var data = {
      username: this.state.username,
      password: this.state.password,
      confirmPassword: this.state.confirmPassword,
    }
    if (data.password === data.confirmPassword) {
      const url = "http://localhost:9000/makeAccount"
      axios.post(url,data)
        .catch(err => alert(err));
      alert("Compte créé !")
    } else {
      alert("Les mots de passe ne correspondent pas...")
    }
  }

  render() {
    return(
      <form onSubmit={this.handleSubmit}>
        <input type="text" name="username" onChange={this.onChange} placeholder="Pseudonyme"/>
        <input type="password" name="password" onChange={this.onChange} placeholder="Mot de passe"/>
        <input type="password" name="confirmPassword" onChange={this.onChange} placeholder="Confirmez le mot de passe"/>
        <button type="submit">Créer un compte</button>
      </form>
    )
  }
}
