import React, {Component} from "react";
import axios from "axios";

export default class Post extends Component {
  state = {
    api: [],
    PostID: this.props.location.state.PostID,
    PTitle: this.props.location.state.PTitle,
    PBody: this.props.location.state.PBody,
    Pdate: this.props.location.state.Pdate,
  }

  componentDidMount() {
    axios.get('http://localhost:9000/blogapi')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }
  render() {
    return(
      <div id="Post">
        <h1>{ PTitle }</h1>
      </div>      
    )
  }
}
