import React, {Component} from "react";
import axios from "axios";

export default class Post extends Component {
  state = {
    api: [],
    PostID: this.props.location.state.id,
    PTitle: this.props.location.state.Title
  }

  componentDidMount() {
    axios.get('http://localhost:9000/blogapi')
      .then(res => {
        const api = res.data;
        this.setState({ api })
      })
  }
  render() {
    return(
      <h1>{this.state.PTitle}</h1>
    )}
}
