import React, { Component } from 'react';
import axios from 'axios'

export default class login extends Component {
  state = {
    username: '',
    password: '',
    confirmPassword: '',
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  onSubmit = (username, password, confirmPassword) => {
    return axios.post('createAccount',
      {username,
        password,
        confirmPassword})
  }

  render() {
    const { username, password, confirmPassword } = this.state;
    return(
      <form method="post">
        <input type="text" name="username" value={username} onChange={this.onChange} placeholder="Pseudonyme"/>
        <input type="password" name="password" value={password} onChange={this.onChange} placeholder="Mot de passe"/>
        <input type="password" name="confirmPassword" value={confirmPassword} onChange={this.onChange} placeholder="Confirmez le mot de passe"/>
        <button onClick={this.onSubmit(this.username, this.password, this.confirmPassword)}>Cr√©er compte</button>
      </form>
    )
  }
}
