import React, {Component} from 'react';

const CommentStyle = {
  display: "flex",
  flexDirection: "column",
  backgroundColor: "#1d2021",
  borderRadius: "15px",
  padding: "3vh",
}

export default class CommentBox extends Component {

  render() {
    return (
      <form action="" style={CommentStyle}>
        <input type="text" placeholder="anonymous"/>
        <input type="text" placeholder="I think you are the worst writer in existence blah blah blah"/>
        <button>Comment</button>
      </form>
    )
  }
}
