import { React, Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {
  state = {
    id: '',
    title: '',
    body: '',
    data: []
  }

  componentDidMount() {
    axios.get('https://jsonplaceholder.typicode.com/posts')
      .then(res => {
        let newData = res.data.slice(0,5);
        this.setState({
          id: newData[newData.length - 1].id + 1,
          data: newData
        }, () => console.log(this.state.id))
        console.log(newData)
      })
      .catch(err => console.log("Couldn't fetch data. Error:" + err ))
  }

  render() {
    return (
      <div className="ArticleContainer">
        <h1>Si ça vous intéresse</h1>
        <div className="AddArticle">
          <b>id de l'article: </b>
          <input type="number" value={this.state.id} />
          <form>
            <input type="text" placeholder='Title' value={this.state.title} />
            <textarea placeholder='Enter Body' value={this.state.body}></textarea>
            <input type="submit" value='Add/Update Post'/>
          </form>
        </div>
        {
          this.state.data.length === 0 ?
            <p>Loading Posts...</p>
          :
            this.state.data.map((post, index) => (
              <article key={index}>
                <h2>{index + 1}. {post.title}</h2>
                <p>{post.body.substr(0, 100)}...</p>
                <button className='delete'>Delete</button>
                <button className='edit'>Edit</button>
              </article>
            ))
        }
      </div>
    )
  }
}

export default App;
